import React from "react"; 
import "./MonCompte.css";
import axios from "axios";
import CreerMonCompte from "./CreerMonCompte/CreerMonCompte";

export default function MonCompte () {

    const [createUserData, setcreateUserData] = React.useState(
        {
            email: "", 
            username: "", 
            password: "", 
            favoritemusicgenre: ""
        }
    );


    function handleChange(event) {
        //console.log(event);
        const { name, value } = event.target;
        setcreateUserData(prevFormData => {
            return {
                ...prevFormData,
                [name]: value
            }
        });
    }

    function handleSubmit(event) {
        event.preventDefault();
        console.log(createUserData);
        axios.post(
            "http://localhost:3001/api/createUser",
            createUserData,
            { headers: { "Content-Type": "application/json" } }
        )
        .then(res => res.data)
        .then(res => console.log(res))
        .catch(err => console.log(err.response))
    }

    return(
        <div>
            <CreerMonCompte handleChange={handleChange} 
                            handleSubmit={handleSubmit} 
                            createUserData={createUserData}/>
        </div>
    );
}
